namespace ScraperBackendService.Configuration;/// <summary>/// Backend service configuration settings./// Defines server hosting options, authentication, and operational parameters./// </summary>public sealed class ServiceConfiguration{    /// <summary>    /// Configuration section name in appsettings.json.    /// </summary>    public const string SectionName = "ServiceConfig";    /// <summary>    /// Service listening port. Default is 8585.    /// </summary>    public int Port { get; set; } = 8585;    /// <summary>    /// Service listening address. Default is 0.0.0.0 (all interfaces).    /// </summary>    public string Host { get; set; } = "0.0.0.0";    /// <summary>    /// Optional authentication token. If empty, authentication is disabled.    /// When set, clients must include this token in request headers.    /// </summary>    public string? AuthToken { get; set; }    /// <summary>    /// Token verification header name. Default is "X-API-Token".    /// </summary>    public string TokenHeaderName { get; set; } = "X-API-Token";    /// <summary>    /// Request timeout duration in seconds. Default is 150 seconds.    /// Applied to both HTTP requests and scraping operations.    /// </summary>    public int RequestTimeoutSeconds { get; set; } = 150;    /// <summary>    /// Global maximum concurrent requests limit for all providers.    /// Controls the maximum number of simultaneous requests to any provider.    /// Default is 3.    /// </summary>    public int MaxConcurrentRequests { get; set; } = 3;    /// <summary>    /// Global rate limit in seconds for all providers.    /// Minimum time interval between consecutive cache-miss requests for each concurrency slot.    /// Set to 0 to disable rate limiting. Default is 30 seconds.    /// </summary>    public int RateLimitSeconds { get; set; } = 30;    /// <summary>    /// Whether to enable aggressive memory optimization including frequent garbage collection.    /// Default is true.    /// </summary>    public bool EnableAggressiveMemoryOptimization { get; set; } = true;    /// <summary>    /// How long to wait for challenge auto-resolution on primary attempts (seconds).    /// Default is 20 seconds.    /// </summary>    public int ChallengeAutoWaitSeconds { get; set; } = 20;    /// <summary>    /// How long to wait for challenge auto-resolution on slow retry attempts (seconds).    /// Default is 45 seconds.    /// </summary>    public int ChallengeAutoWaitSlowSeconds { get; set; } = 45;}